/** @jsxRuntime classic */
/** @jsx jsx */
/*import { jsx, css } from "@emotion/react"; */
import React, { useRef, useEffect, useState } from "react";
import { useWeatherContext } from "../../context/WeatherContextProvider";
import mapboxgl from "!mapbox-gl"; // eslint-disable-line import/no-webpack-loader-syntax

mapboxgl.accessToken =
  "pk.eyJ1IjoiZ3JlZzg4cGwiLCJhIjoiY2t5NmEzY2l4MHR3MzJwb2pyZTFmdjFwcSJ9.TfLfzZj270_0ZbXFeoMpLA";

export const Map = () => {
  const { weather } = useWeatherContext();
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(21.01);
  const [lat, setLat] = useState(52.23);
  const [zoom, setZoom] = useState(11);

  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: "mapbox://styles/mapbox/streets-v11",
      center: [lng, lat],
      zoom: zoom,
    });
  });

  return (
    <div>
      {/* <div>Lon: {weather.coord.lon}</div>
      <div>Lat: {weather.coord.lat}</div> */}

      <div
        ref={mapContainer}
        styleName="map-container" /*css={mapContainer}*/
      />
    </div>
  );
};

const mapContainer = css`
  height: 100vh;
`;
