import React from "react";
import { useWeatherContext } from "../../context/WeatherContextProvider";

export const Weather = () => {
  const { weather } = useWeatherContext();

  //   const sunrise = new Date(weather.sys.sunrise);
  //   console.log(sunrise);

  function convertTime(unixTime, offset) {
    let dt = new Date((unixTime + offset) * 1000);
    let h = dt.getHours() - 1;
    let m = "0" + dt.getMinutes();
    let t = h + ":" + m.substr(-2);
    return t;
  }

  let sRise = convertTime(weather.sys.sunrise, weather.timezone);
  let sSet = convertTime(weather.sys.sunset, weather.timezone);

  console.log(sRise);
  console.log(sSet);

  const iconId = weather.weather[0].icon;

  return (
    <div>
      <div>Miasto: {weather.name}</div>
      <div>Temperatura: {weather.main.temp} &deg;C </div>
      <div>Wilgotność: {weather.main.humidity} %</div>
      <div>Ciśnienie: {weather.main.pressure} hPa</div>
      <div> Wschód słońca: sRise</div>
      <div>Opis: {weather.weather[0].main}</div>
      <div>Opis szczegółowy: {weather.weather[0].description}</div>
      <img
        src={`http://openweathermap.org/img/w/${iconId}.png`}
        alt={weather.weather[0].description}
      />
    </div>
  );
};
